import React from 'react'
import DropDown from '../Button/DropDown'
import Update from '../Button/Update';
import Week from '../Button/Week';
import WeeklyUpdate from './WeeklyUpdate';

const ForUpdate = () => {

  const [isShown, setIsShown] = useState(false);

  const handleClick = event => {

    setIsShown(current => !current);
  };

  return (
    <div className='ForUpdate'>
      <div className='ForUpdate_Month'>
        <DropDown/>
      </div>
      <div className='ForUpdate_Week'>
         <Week/>
       </div>
        <div className='ForUpdate_btn'>
        <Update onClick={handleClick}/>
       </div>  
       {isShown && <WeeklyUpdate />}


    </div>
  )
}

export default ForUpdate;
