import React, { useState,useContext } from 'react'
import DropDown from '../Assests/images/DropDown.svg';
import Inputarea from './Inputarea'
import { Context } from './Inputarea';


const CommonDropDown = (props) => {
  const[perform,setPerform] = useState(false);
  // const[placeholder,setPlaceholder] = useState("No.of resources used")
  const val=useContext(Context);

  return  (
   <div>

    <div className='SelectField'>

     <span className='SelectField_Placeholder'>{val}</span>


    <img className='SelectField_Arrow'
    src={DropDown}

    onClick={()=>setPerform(!perform)}


    />


    </div>
    <Inputarea output={perform} />
    </div>
  )
}

export default CommonDropDown
