import React,{useState} from 'react'
import WeeklyUpdate from '../ProjectDetailsPage/WeeklyUpdate';

const Update = () => {
  const[shownOn,setShownOn]=useState(false);
  return (
    <div>
    <div >
         <button className='Updatebtn' onClick={()=>setShownOn(true)}>Update</button>   

    </div>
    <WeeklyUpdate trigger={shownOn} /> 
    </div>
  )

}

export default Update
