import React, { useState,useEffect } from 'react'
import Cancel from '../Button/Cancel';


const WeeklyUpdate = (props) => {
  const initialValues = {
    TMFixedCost:"",
    resourcesused:"",
    WeeklyCompletion:"",
    FeaturesStories:"",
    FeaturesCompleted:"",
    NewBugs:"",
    Bugs:"",
    CodeReview:"",
    UnitTesting:"",
    WeeklyCommunication:"",
    Dependencies:"",
    RisksMitigation:"",
    Risks:"",
    SupportRequired:""
}
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const handleChange=(e)=>{
    const{name,value}=e.target;
    setFormValues({...formValues,[name]:value});
  };

  const handleSubmit=(e)=>{
    e.preventDefault();
    setFormValues(validate(formValues)) ;
    setIsSubmit(true);
   };
   useEffect(()=>{
    if(Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);
   }
  },[formValues]);

   const validate=(values)=>{
     const errors = {};
     if(!values.TMFixedCost)
     {
       errors.TMFixedCost = "*Required";
     }
     if(!values.WeeklyCompletion)
     {
       errors.WeeklyCompletion = "*Required";
     }
     if(!values.FeaturesCompleted)
     {
       errors.FeaturesCompleted= "*Required";
     }

     if(!values.Dependencies)
     {
       errors.Dependencies = "*Required";
     }
     if(!values.RisksMitigation)
     {
       errors.RisksMitigation = "*Required";
     }
     if(!values.Risks)
     {
       errors.Risks = "*Required";
     }
     if(!values.SupportRequired)
     {
       errors.SupportRequired = "*Required";
     }

   }


  return (props.trigger) ? (

    <form style={{
      position: "absolute",
      width: "100%",
      top: "2rem",
      left: "0%",
      backgroundColor: "rgba(0,0,0,0.5",
    }} 
    onSubmit={handleSubmit} 
  >


      <div className='WeeklyUpdate'>

        <div className='WeeklyUpdate_Head'>
          <h1 className='WeeklyUpdate_Head_Title'>Weekly Update</h1>
        </div>



        <div className='WeeklyUpdate_Overalldetail'>

          <h1 className='WeeklyUpdate_Overalldetail_Title'>Overall Detail</h1>

          <div className='WeeklyUpdate_Overalldetail_Field'>
            <div className='WeeklyUpdate_Overalldetail_Field_All'>
              <label className='WeeklyUpdate_Overalldetail_Field_All_label'>T&M/Fixed Cost</label><br />
              <input className="WeeklyUpdate_Overalldetail_Field_All_input" 
                     type="text" 
                     placeholder='Enter T&M/Fixed Cost '
                       onChange={handleChange} 
                    />
            </div>
            <p>{formErrors.TMFixedCost}</p>

            <div className='WeeklyUpdate_Overalldetail_Field_All' >
              <label className='WeeklyUpdate_Overalldetail_Field_All_label'>No.of resources used</label><br />
              <select className="WeeklyUpdate_Overalldetail_Field_All_input" 
                      type="text" />
            </div>

            <div className='WeeklyUpdate_Overalldetail_Field_All' >
              <label className='WeeklyUpdate_Overalldetail_Field_All_label'>Weekly% Completion</label><br />
              <input className="WeeklyUpdate_Overalldetail_Field_All_input" 
                     type="text"
                     onChange={handleChange}  />
            </div>
          </div>

        </div>




        <div className='WeeklyUpdate_Weeklydetail'>

          <h1 className='WeeklyUpdate_Weeklydetail_Title'>Weekly Detail</h1>

          <div className='WeeklyUpdate_Weeklydetail_Field'>

            <div className='WeeklyUpdate_Weeklydetail_Field_All'>
              <label className='WeeklyUpdate_Weeklydetail_Field_All_label'>No.of Features/Stories</label><br />
              <select className="WeeklyUpdate_Weeklydetail_Field_All_input" type="text" placeholder='Enter No.of Features/Stories' />
            </div>

            <div className='WeeklyUpdate_Weeklydetail_Field_All'>
              <label className='WeeklyUpdate_Weeklydetail_Field_All_label'>Features Completed</label><br />
              <input className="WeeklyUpdate_Weeklydetail_Field_All_input" 
                     type="text"
                       onChange={handleChange}
                    />
            </div>

            <div className='WeeklyUpdate_Weeklydetail_Field_All' >
              <label className='WeeklyUpdate_Weeklydetail_Field_All_label'>New Bugs</label><br />
              <select className="WeeklyUpdate_Weeklydetail_Field_All_input" 
                      type="text" />
            </div>

            <div className='WeeklyUpdate_Weeklydetail_Field_All'>
              <label className='WeeklyUpdate_Weeklydetail_Field_All_label'>Bugs</label><br />
              <select className="WeeklyUpdate_Weeklydetail_Field_All_input" 
                      type="text" />
            </div>

            <div className='WeeklyUpdate_Weeklydetail_Field_All'>
              <label className='WeeklyUpdate_Weeklydetail_Field_All_label'>Code Review</label><br />
              <select className="WeeklyUpdate_Weeklydetail_Field_All_input" 
                      type="text" />
            </div>

            <div className='WeeklyUpdate_Weeklydetail_Field_All'>
