        .then((response) => {

          setWeeklyReport(response?.data);
          console.log("report1",response.data)


          console.log("weekpost",response)


        })

        .catch(error => {
          console.log("error",error);
        })





    console.log("report",weeklyReport)


    return (

        <div>

        <div className='ForUpdate'>
       <div className='ForUpdate_Year'>

        <div className="ForUpdate_Year_Month">
           <span className='ForUpdate_Year_Month_Placeholder'>jANUARY</span>
           <img className='ForUpdate_Year_Month_DropDown' src={MonthsDropDown} />
       </div>



         <div className='ForUpdate_Year_Week_btn'>
             <button className='ForUpdate_Year_Week_btn_1'  onClick={()=>weekUpdate(1)}>Week 1</button>
