             <button className='ForUpdate_Year_Week_btn_4'  onClick={()=>weekUpdate(4)}>Week 4</button>
         </div>


      </div>
        <div className='ForUpdate_btn'>
        <button className='ForUpdate_btn_1' onClick={()=>setShownOff(true)}>Update</button> 
        </div>  



    </div>
    <WeeklyUpdate trigger={shownOff} setShownOff={setShownOff}/>

        <div className='ProjectDescription-All'>

            <div className='ProjectDescription_Data' >



                   {console.log("divum",Object.values(weeklyReport))}


