import React, { useState, useEffect } from 'react'
import Cancel from '../Button/Cancel';


const WeeklyUpdate = (props) => {
  const initialValues = {
    TMFixedCost: "",
    resourcesused: "",
    WeeklyCompletion: "",
    FeaturesStories: "",
    FeaturesCompleted: 0,
    NewBugs: "",
    Bugs: "",
    CodeReview: "",
    UnitTesting: "",
    WeeklyCommunication: "",
    Dependencies: "",
    RisksMitigation: "",
    Risks: "",
    SupportRequired: ""
  }
  const [formValues, setFormValues] = useState(initialValues);
  const [validation, setValidation] = useState({});




  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormValues({ ...formValues, [name]: value });
    console.log(formValues);
  };



  const checkValidation=()=>{



    let errors = validation;
    if (!formValues.TMFixedCost.trim()) {
      errors.TMFixedCost = "*Required";
    }
    else {
      errors.TMFixedCost = "";
    }
    if (!formValues.WeeklyCompletion.trim()) {
      errors.WeeklyCompletion = "*Required";
    }
    else {
      errors.WeeklyCompletion = "";
    }
    if (!formValues.FeaturesCompleted===0) {
      errors.FeaturesCompleted = "*Required";
    }

    else {
      errors.FeaturesCompleted = "";
    }

    if (!formValues.Dependencies.trim()) {
      errors.Dependencies = "*Required";
    }
    else {
      errors.Dependencies = "";
    }
    if (!formValues.RisksMitigation.trim()) {
      errors.RisksMitigation = "*Required";
    }
    else {
      errors.RisksMitigation = "";
    }
    if (!formValues.Risks.trim()) {
      errors.Risks = "*Required";
    }
    else {
      errors.Risks = "";
    }
    if (!formValues.SupportRequired.trim() ){
      errors.SupportRequired = "*Required";
    }
    else {
      errors.SupportRequired = "";
    }

    return setValidation(errors);
  };



  const handleSubmit = (e) => {
    e.preventDefault();
    checkValidation();
  }






  return (props.trigger) ? (

    <form style={{
      position: "absolute",
      width: "144rem",
      top: "2rem",
      left: "10.5%",
      backgroundColor: "rgba(0,0,0,0.5",
    }}
      onSubmit={handleSubmit}
    >


      <div className='WeeklyUpdate'>

        <div className='WeeklyUpdate_Head'>
