import React from 'react'
import WeeklyUpdate from '../ProjectDetailsPage/WeeklyUpdate';

const Update = () => {
  const[shownOff,setShownOff]=useState(false);
  return (
    <div >
         <button className='Updatebtn' onClick={()=>setShownOff(true)}>Update</button>   
         <WeeklyUpdate trigger={shownOff}  /> 
    </div>
  )
}

export default Update
