import React,{useState} from 'react'
import DropDown from '../Button/DropDown'
import Update from '../Button/Update';
import Week from '../Button/Week';
import WeeklyUpdate from '../WeeklyForm/WeeklyUpdate';
import './ForUpdate.scss';

const ForUpdate = () => {
  let count=0
  const[shownOff,setShownOff]=useState(false);
  let[weeklyUpdate,setWeeklyUpdate] = useState({
    Month:0,
    Week:0
  })


const filterMethod=()=>{
  weeklyUpdate.Month=weeklyUpdate.Month+1;
  setWeeklyUpdate(weeklyUpdate.Month+1);

}

console.log("MOnth",weeklyUpdate);



  return (
   <div>
    <div className='ForUpdate'>
      <div className='ForUpdate_Year'>

        <div className="ForUpdate_Year_Month">
        <select onClick={filterMethod} className='ForUpdate_Year_Month_All'>
                <option value={weeklyUpdate.Month} >January</option>
                <option >February</option>
                <option >March</option>
                <option>April</option>
                <option>May</option>
                <option>June</option>
                <option>July</option>
                <option>August</option>
                <option>September</option>
                <option>October</option>
                <option>November</option>
                <option>December</option>

            </select>
       </div>


         <div className='ForUpdate_Year_Week_btn'>
             <button className='ForUpdate_Year_Week_btn_1'>Week 1</button>
             <button className='ForUpdate_Year_Week_btn_2'>Week 2</button>
             <button className='ForUpdate_Year_Week_btn_3'>Week 3</button>
             <button className='ForUpdate_Year_Week_btn_4'>Week 4</button>
         </div>


      </div>
        <div className='ForUpdate_btn'>
        <button className='ForUpdate_btn_1' onClick={()=>setShownOff(true)}>Update</button> 
        </div>  



    </div>
    <WeeklyUpdate trigger={shownOff} setShownOff={setShownOff} />
    </div> 

  )
}

export default ForUpdate;
