import React, { useEffect, useState } from 'react'
import Definition from '../Definition/Definition'
import DescriptionData from './DescriptionData'
import { useParams } from "react-router-dom";
import axios from 'axios'
import './ProjectDescription.scss'

const ProjectDescription = () => {

    const [description, setDescription] =useState([]);
    const [otherDetails, setOtherDetails]=useState([]);
    const params = useParams();
// const Description=[{
//         TMfixedCost: "3938493",
//         resourcesUsed:10,
//         weeklyCompletion: 45,
//         numberOfStories: 45,
//         featuresCompleted: 34,
//         newBugs: 45,
//         bugsFixed: 34,
//         codeReview: true,
//         unitTesting: true,
//         weeklyCommunication: false,
//         delay : 10,
// }]

 const others= [{
            dependencies: " Dependencies in project management deal with managing and scheduling project tasks while keeping their sequences and requirements in mind. If task B requires the completion of task A,",
            riskMitigation: " No RiskMitigation",
            risks: "No Risks in the Week",
            supportRequired: <ul><li> Uptime requirements for a service.</li>
                                 <li>Data storage capacity per customer.</li>
                                 <li> Back-up processes for managing risks.</li></ul>
        }
    ]


     const getDetails =()=>{ 
        axios.get('http://13.233.18.99:3010/api/v1/reports/getWeeklyReport/3399d75686ee45b3a5943f29332ed389')
        .then((response)=>{
            setDescription(response.data)
            console.log("api",response.data);
        })
        .catch(error=>console.log(error))

        axios.get(`http://13.233.18.99:3010/api/v1/reports/get_other_weekly_reports_data/${params.id}`)
        .then((res)=>{
            setOtherDetails(response.data)
            console.log("otherapi",response.data);
        })
    }

    useEffect(()=>{

        getDetails();
    },[]);






    return (
        <div className='ProjectDescription-All'>
            <div className='ProjectDescription_Data' >

                {console.log("dummy",description)}

                 {description.content && description.content.map((data) =>
                    <DescriptionData data={data} />)}



            </div>
             <div>
                <div >
                    {otherDetails && otherDetails.map((value) =>

                        <Definition value={value} />)}
                </div>
            </div> 
        </div>


    )
}
export default ProjectDescription;
