import React,{useState,useEffect,createContext} from 'react'
import axios from 'axios'
import {
    useSearchParams
  } from "react-router-dom";

import ProjectCardData from './ProjectCardData';
import './ProjectCardData.scss';
import WeeklyUpdate from '../WeeklyForm/WeeklyUpdate';
import ForUpdate from '../ForUpdate/ForUpdate';


export const ProjectId=createContext();
const ProjectCard = () => {


    const [searchParams] = useSearchParams();
    const idvalue=searchParams.get('id');
    const details=idvalue.slice(1,33)


    const [dashboardData,setDasboardData] = useState([]);
    // const dashboardData=[{
    //     projectName : "Cipla",
    //     logoUrl : "https://i.postimg.cc/Z9Wbf1Lk/stock-illustration-awesome-abstract-consulting-design-template-logo-icon-isolated-free.jpg",
    //     status : "INPROGRESS",
    //     startDate : "01-01-2022",
    //     endDate : "01-12-2022" ,
    //     numberOfBugs : 1000,
    //     numberOfDependencies : "45%",
    //     codeReviews : "21%",
    //     unitTesting :"10%",
    //     delay : 45
    // },
    // ]

     const getProjectDetails =()=>{ 
         axios.get('http://13.233.18.99:3010/api/v1/projects/getOverallproject')
         .then((response)=>{
            setDasboardData(response.data)
             console.log("api",response.data);
         })
         .catch(error=>console.log(error))


    }
    useEffect(()=>{

        getProjectDetails();


    },[]);





     for(let i=0;i<dashboardData?.content?.projects.length;i++){

     if(details===dashboardData?.content?.projects[i].projectId){
    return (

        <div>


                <ProjectCardData 
                numberOfBugs={dashboardData?.content?.projects[i].numberOfBugs} 
                dependencies={dashboardData?.content?.projects[i].dependencies}
                codeReviews={dashboardData?.content?.projects[i].codeReviews} 
                unitTesting={dashboardData?.content?.projects[i].unitTesting}
                delay={dashboardData?.content?.projects[i].delay}/>


        </div>
    )


                }
            }

             <ProjectId.Provider value={{details}}>


              <WeeklyUpdate />

               {console.log("details",details)}

            </ProjectId.Provider>
            }



export default ProjectCard;
