import React, { useEffect, useState, useContext } from "react";
import Definition from "../Definition/Definition";
import DescriptionData from "./DescriptionData";
import { useSearchParams } from "react-router-dom";
import axios from "axios";
import "./ProjectDescription.scss";
import UserContext from "../UserContext";
import WeeklyUpdate from "../WeeklyForm/WeeklyUpdate";
import MonthsDropDown from "../../Assests/images/MonthsDropDown.png";
import "./ForUpdate.scss";

const ProjectDescription = () => {
  const [weak, setWeak] = useState();
  const [monthUpdate, setMonthUpdate] = useState();
  const [shownOff, setShownOff] = useState(false);
  const [month, setMonth] = useState("January");
  const [display, setDisplay] = useState(false);
  const [weeklyReport, setWeeklyReport] = useState({});
  const [searchParams] = useSearchParams();

  const idvalue = searchParams.get("id");
  const details = idvalue.slice(1, 33);
  const Month = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  // const Description=[{
  //         TMfixedCost: "3938493",
  //         resourcesUsed:10,
  //         weeklyCompletion: 45,
  //         numberOfStories: 45,
  //         featuresCompleted: 34,
  //         newBugs: 45,
  //         bugsFixed: 34,
  //         codeReview: true,
  //         unitTesting: true,
  //         weeklyCommunication: false,
  //         delay : 10,
  // }]

  const others = [
    {
      dependencies:
        " Dependencies in project management deal with managing and scheduling project tasks while keeping their sequences and requirements in mind. If task B requires the completion of task A,",
      riskMitigation: " No RiskMitigation",
      risks: "No Risks in the Week",
      supportRequired: (
        <ul>
          <li> Uptime requirements for a service.</li>
          <li>Data storage capacity per customer.</li>
          <li> Back-up processes for managing risks.</li>
        </ul>
      ),
    },
  ];


  const weekUpdate = () => {
    axios
      .post("http://13.233.18.99:3010/api/v1/reports/getWeeklyReport", {
        ProjectId: "c5c100b648cd40c0a27d1e61a6811952",
        Month: monthUpdate,
        Week: weak,
      })
      .then((response) => {
        setWeeklyReport(response?.data);
        console.log("report1", response.data);
        console.log("as", weak);
        // console.log("Month", monthUpdate);
        console.log("weekpost", response);
      })

      .catch((error) => {
        console.log("error", error);
      });
     console.log('week',weak);
     console.log('Month',monthUpdate);

  };
  useEffect(() => {
    weekUpdate();
  }, [weak,monthUpdate]);

  console.log("report", weeklyReport);

//    const Addmonth=(value)=>{
//     console.log(value);

//     // console.log((Month.findIndex(value)));
//     setMonthUpdate(Month.indexOf(value)+1)
//     console.log(Month.indexOf(value)+1);

//     console.log(monthUpdate);


//    }

  return (
    <div>
      <div className="ForUpdate">
        <div className="ForUpdate_Year">
          <div className="ForUpdate_Year_Month">
            <span className="ForUpdate_Year_Month_Placeholder">{month}</span>
            <img
              className="ForUpdate_Year_Month_DropDown"
              src={MonthsDropDown}
              onClick={() => setDisplay(!display)}
            />
          </div>

          <div className="ForUpdate_Year_Week_btn">
            <button
              className="ForUpdate_Year_Week_btn_1"
              onClick={(previous) => {
                setWeak(previous=1);
              }}
            >
              Week 1
            </button>
            <button
              className="ForUpdate_Year_Week_btn_2"
              onClick={() => {
                setWeak(2);
              }}
            >
              Week 2
            </button>
            <button
              className="ForUpdate_Year_Week_btn_3"
              onClick={(previous) => {
                setWeak(previous=3);
              }}
            >
              Week 3
            </button>
            <button
              className="ForUpdate_Year_Week_btn_4"
              onClick={() => {
                setWeak(4);
              }}
            >
              Week 4
            </button>
          </div>
        </div>
        <div className="ForUpdate_btn">
          <button className="ForUpdate_btn_1" onClick={() => setShownOff(true)}>
            Update
          </button>
        </div>
      </div>

      {display ? (
        <div className="Month">
          {Month.map((monthName) => {
            return (
              <span className="Month_All" onClick={(previous) => {setMonthUpdate( previous=Month.indexOf(monthName)+1);setMonth(monthName)}}>
                {" "}
                {monthName}
              </span>
            );
          })}
        </div>
      ) : (
        ""
      )}

      <WeeklyUpdate trigger={shownOff} setShownOff={setShownOff} />

      <div className="ProjectDescription-All">
        <div className="ProjectDescription_Data">
          {console.log("divum", Object.values(weeklyReport))}

          {/* {Object.values(weeklyReport) &&
            Object.values(weeklyReport).map((data, index) => (
              <DescriptionData key={index} datas={data} />
           ))} */}

           <DescriptionData 
          //  TMfixedCost={weeklyReport?.content?.weeklyDetail?.T&M-fixedCost} 
           weeklyCompletion={weeklyReport?.content?.weeklyDetail?.weeklyCompletion}    
           numberOfStories={weeklyReport?.content?.weeklyDetail?.bugsFixed}   
           featuresCompleted={weeklyReport?.content?.weeklyDetail?.featuresCompleted}
           newBugs={weeklyReport?.content?.weeklyDetail?.newBugs}
           bugsFixed={weeklyReport?.content?.weeklyDetail?.bugsFixed}
           codeReview={weeklyReport?.content?.weeklyDetail?.codeReview}
           unitTesting={weeklyReport?.content?.weeklyDetail?.unitTesting}
           weeklyCommunication={weeklyReport?.content?.weeklyDetail?.weeklyCommunication}
           bugsFixed={weeklyReport?.content?.weeklyDetail?.bugsFixed}
           /> 


        </div>
        <div>
          {others && others.map((value) => <Definition value={value} />)}
        </div>
      </div>
    </div>
  );
};
export default ProjectDescription;
