import React,{useEffect, useState} from 'react'
import DropDown from '../Button/DropDown'
import Update from '../Button/Update';
import Week from '../Button/Week';
import axios from "axios"
import WeeklyUpdate from '../WeeklyForm/WeeklyUpdate';
import './ForUpdate.scss';

const ForUpdate = () => {
  let count=0
  const[shownOff,setShownOff]=useState(false);
  const[month,setMonth]=useState(0);
  const[week,setWeek]=useState(0);

  const months=()=>{
    console.log("January")

    week=1;
     setMonth(1);


  }
  const weekupdate=(id)=>{

    if(id==="1")
    {

      setWeek(1);

      }
    if(id==="2")
    {

      setWeek(2);

    }
    if(id==="3")
    {

      setWeek(3);


    }
    if(id==="4")
    {

      setWeek(4);


    }

  }






// console.log("MOnth",month);

   console.log("Week",week);
   axios.post("http://localhost:3010/api/v1/reports/getWeeklyReport",
   {

       "projectId": "393e903ie0kdsdosd0od",
       "month": 1,
       "weekNo": week,


   })
   .then((response) => {


     console.log(response)

   })
   .catch(error => {
     console.log(error);
   })



  return (
   <div>
    <div className='ForUpdate'>
      <div className='ForUpdate_Year'>

        <div className="ForUpdate_Year_Month">
        <select onChange={months} className='ForUpdate_Year_Month_All'>
                <option>January</option>
                <option>February</option>
                <option>March</option>
                <option>April</option>
                <option>May</option>
                <option>June</option>
                <option>July</option>
                <option>August</option>
                <option>September</option>
                <option>October</option>
                <option>November</option>
                <option>December</option>

            </select>
       </div>



         <div className='ForUpdate_Year_Week_btn'>
             <button className='ForUpdate_Year_Week_btn_1'  onClick={()=>weekupdate("1")}>Week 1</button>
             <button className='ForUpdate_Year_Week_btn_2'  onClick={()=>weekupdate("2")}>Week 2</button>
             <button className='ForUpdate_Year_Week_btn_3'  onClick={()=>weekupdate("3")}>Week 3</button>
             <button className='ForUpdate_Year_Week_btn_4'  onClick={()=>weekupdate("4")}>Week 4</button>
         </div>


      </div>
        <div className='ForUpdate_btn'>
        <button className='ForUpdate_btn_1' onClick={()=>setShownOff(true)}>Update</button> 
        </div>  



    </div>
    <WeeklyUpdate trigger={shownOff} setShownOff={setShownOff} />
    </div> 

  )
}

export default ForUpdate;
