import { useState, useEffect, useRef } from "react";
import "./newproject.scss";

function Newprojectform(props) {
  const { trigger, setShowForm } = props;
  const [FileName, setFileName] = useState("");
  //function for add logo -start

  const inputFileRef = useRef(null);
  const onFilechange = (e) => {
    setFileName(e.target.files[0].name);
  };
  const onBtnClick = () => {
    inputFileRef.current.click();
  };

  //---------------------------End-----------------------//

  //form validation

  const [formValues, setFormValue] = useState({
    ProjectName: "",
    fixedcost: "",
    startdate: "",
    duration: "",
    totalresources: "",
    projectcompletetion: "",
    start_date: "",
    end_date: "",
    features: "",
    bugs: "",
    selectedFile: "",

    // ProjectName: {
    //   value: "",
    //   isRequired: "",
    //   error: "",
    // },
    // fixedcost: {
    //   value: "",
    //   isRequired: "",
    //   error: "",
    // },
    // startdate: {
    //   value: "",
    //   isRequired: "",
    //   error: "",
    // },
    // duration: {
    //   value: "",
    //   isRequired: "",
    //   error: "",
    // },
    // totalresources: {
    //   value: "",
    //   isRequired: "",
    //   error: "",
    // },
    // projectcompletetion: {
    //   value: "",
    //   isRequired: "",
    //   error: "",
    // },
    // start_date: {
    //   value: "",
    //   isRequired: "",
    //   error: "",
    // },
    // end_date: {
    //   value: "",
    //   isRequired: "",
    //   error: "",
    // },
    // features: {
    //   value: "",
    //   isRequired: "",
    //   error: "",
    // },
    // bugs: {
    //   value: "",
    //   isRequired: "",
    //   error: "",
    // },
    // selectedFile: {
    //   value: "",
    //   isRequired: "",
    //   error: "",
    // },
  });
  const [formErrors, setFormError] = useState(false);
  const [isSubmit, setIsSubmit] = useState(false);

  const handleChange = (e, type) => {
    const { name, value } = e.target;
    setFormValue({ ...formValues, [type]: value });
    console.log(formValues);
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    // setFormValue(formValues);
    setFormError(validate(formValues));
  };

  useEffect(() => {
    if (isSubmit) {
      setFormError(validate(formValues));
    }
  }, [formValues]);

  const validate = (values) => {
    const errors = {};
    if (!values.ProjectName.trim()) {
      errors.ProjectName = "Please Enter  Project Name";
    }
    if (!values.fixedcost.trim()) {
      errors.fixedcost = "Please Enter  T&M/Fixed Cost";
    }
    if (values.fixedcost < 0) {
      errors.fixedcost = "T&M/Fixed Cost shouldn't be negative";
    }
    if (!values.startdate.trim()) {
      errors.startdate = "Please Enter Date";
    }
    if (!values.duration.trim()) {
      errors.duration = "Please Enter Duration Value";
    }
    if (values.duration < 0) {
      errors.duration = "Duration shouldn't be negative";
    }
    if (!values.totalresources.trim()) {
      errors.totalresources = "Please Enter total resources";
    }
    if (!values.projectcompletetion.trim()) {
      errors.projectcompletetion = "Please Enter project completion %";
    }
    if (values.projectcompletetion < 0) {
      errors.projectcompletetion = "Duration shouldn't be negative";
    }
    if (values.projectcompletetion > 100) {
      errors.projectcompletetion = "Percent shouldn't be greater than 100";
    }
    if (!values.start_date.trim()) {
      errors.start_date = "Please Enter Date";
    }
    if (!values.end_date.trim()) {
      errors.end_date = "Please Enter Date";
    }
    if (!values.bugs.trim()) {
      errors.bugs = "Please Enter number of bugs ";
    }
    if (values.bugs < 0) {
      errors.bugs = "Bugs shouldn't be negative";
    }
    if (!values.features.trim()) {
      errors.features = "Please Enter the feature value";
    }
    setIsSubmit(true);
    return errors;
  };

  //-----------------validation end---------------------//
  return trigger ? (
    <>
      {console.log("formErrors", formErrors)}
      <form className="new-project-form" onSubmit={handleSubmit}>
        <div className="new-project-form-container">
          <div className=" new-project-form-container__form-heading">
            <span className="heading"> Create New Project</span>
          </div>
          <div className="new-project-form-container__logo-container">
            <input
              type="file"
              className="logo-form"
              ref={inputFileRef}
              onChange={onFilechange}
              hidden
            />
            <button className="logo-btn" onClick={onBtnClick}>
              +
            </button>

            <div className="add-logo">Add logo</div>
            <span className="UploadedFileName">{FileName}</span>
          </div>
          <span className="new-project-form-container__Overall-Detail">
            Overall Detail
          </span>
          <div className="line-one">
            <div className="first">
              <label htmlFor="Project Name" className="labels">
                Project Name
              </label>
              <br />

              <input
                id="Project Name"
                className="inputs"
                type="text"
                value={formValues.ProjectName}
                onChange={(e) => handleChange(e, "ProjectName")}
                placeholder="Enter Project Name"
              />
              {formErrors.ProjectName && (
                <p className="errors">{formErrors.ProjectName}</p>
              )}
            </div>
            <div className="second">
              <label htmlFor="date" className="labels">
                Project Start Date
              </label>
              <br />
              <input
                id="date"
                className="inputs"
                type="date"
                value={formValues.startdate}
                onChange={(e) => handleChange(e, "startdate")}
                placeholder="Enter Project Start Date"
              />
              {formErrors.startdate && (
                <p className="errors">{formErrors.startdate}</p>
              )}
            </div>
          </div>
          <div className="line-one">
            <div className="first">
              <label htmlFor="Duration" className="labels">
                Duration
              </label>
              <br />

              <input
                id="Duration"
                className="inputs"
                type="number"
                value={formValues.duration}
                onChange={(e) => handleChange(e, "duration")}
                placeholder="Enter Duration"
              />
              <p className="errors">{formErrors.duration}</p>
            </div>
            <div className="second">
              <label htmlFor="totalresources" className="labels">
                Total resources
              </label>
              <br />
              <input
                id="totalresources"
                className="inputs"
                type="number"
                value={formValues.totalresources}
                onChange={(e) => handleChange(e, "totalresources")}
                placeholder=""
              />
              <p className="errors">{formErrors.totalresources}</p>
            </div>
          </div>
          <div className="line-one">
            <div className="first">
              <label htmlFor="project-completion" className="extralabel">
                Overall Project% Completion
              </label>
              <br />

              <input
                id="project-completion"
                className="inputs"
                onChange={(e) => handleChange(e, "projectcompletetion")}
                type="number"
                value={formValues.projectcompletetion}
                placeholder=""
              />
              <p className="errors">{formErrors.projectcompletetion}</p>
            </div>
            <div className="second">
              <label htmlFor="fixedcost" className="labels">
                T&M/Fixed Cost
              </label>
              <br />

              <input
                id="fixedcost"
                className="inputs"
                type="number"
                value={formValues.fixedcost}
                onChange={(e) => handleChange(e, "fixedcost")}
                placeholder="Enter T&M/Fixed Cost"
              />
              {formErrors.fixedcost && (
                <p className="errors">{formErrors.fixedcost}</p>
              )}
            </div>
          </div>
          <span className="Weekly-Detail">Weekly Detail</span>
          <div className="line-one">
            <div className="first">
              <label htmlFor="start-date" className="labels">
                Start Date
              </label>
              <br />

              <input
                id="start-date"
                className="inputs"
                type="date"
                value={formValues.start_date}
                onChange={(e) => handleChange(e, "start_date")}
                placeholder="Enter Start Date"
              />
              <p className="errors">{formErrors.start_date}</p>
            </div>
            <div className="second">
              <label htmlFor="end-date" className="labels">
                End Date
              </label>
              <br />
              <input
                id="end-date"
                className="inputs"
                type="date"
                value={formValues.end_date}
                onChange={(e) => handleChange(e, "end_date")}
                placeholder="Enter End Date"
              />
              <p className="errors">{formErrors.end_date}</p>
            </div>
          </div>
          <div className="line-one">
            <div className="first">
              <label htmlFor="features" className="labels">
                No.of Features/Stories
              </label>
              <br />

              <input
                id="features"
                className="inputs"
                onChange={(e) => handleChange(e, "features")}
                type="number"
                value={formValues.features}
                placeholder=""
              />
              <p className="errors">{formErrors.features}</p>
            </div>
            <div className="second">
              <label htmlFor="bugs" className="labels">
                No.of Bugs
              </label>
              <br />
              <input
                id="bugs"
                className="inputs"
                type="number"
                value={formValues.bugs}
                onChange={(e) => handleChange(e, "bugs")}
                placeholder="Enter Bugs here"
              />
              <p className="errors">{formErrors.bugs}</p>
            </div>
          </div>
          <div className="form-btn">
            <button
              className="cancel-btn"
              onClick={(e) => {
                setShowForm(false);
              }}
            >
              Cancel
            </button>
            <button className="done-btn">Done</button>
          </div>
        </div>
      </form>
    </>
  ) : (
    ""
  );
}

export default Newprojectform;
