import React, { useState, useEffect } from 'react'
// import ProjectDetails from '../../Pages/ProjectDetailsPage/DetailsPage';
import Cancel from '../Button/Cancel'
import CommonDropDown from '../CommonDropDown';
import Inputarea from '../Inputarea';
import './WeeklyUpdate.scss'


const WeeklyUpdate = (props) => {

  const [formValues, setFormValues] = useState({
    TMFixedCost: "",
    WeeklyCompletion: "",
    numberOfStories:"",
    FeaturesCompleted: "",
    newBugs:"",
    bugsFixed:"",
    codeReview:"",
    unitTesting:"",
    weeklyCommunication:"",
    delay:"",
    Dependencies: "",
    RisksMitigation: "",
    Risks: "",
    SupportRequired: ""
  });
  const [formErrors, setFormErrors] = useState({
    TMFixedCost: "",
    WeeklyCompletion: "",
    numberOfStories:"",
    FeaturesCompleted: "",
    newBugs:"",
    bugsFixed:"",
    codeReview:"",
    unitTesting:"",
    weeklyCommunication:"",
    delay:"",
    Dependencies: "",
    RisksMitigation: "",
    Risks: "",
    SupportRequired: ""})
  const [submit, setSubmit] = useState(false);




  const handleChange = (event, type) => {

    const { name, value } = event.target;
    setFormValues({ ...formValues, [type]: value });
  }

  useEffect(()=>{

    if(submit){
      setFormErrors(validate(formValues));
    }


  },[formValues]);




  const handleSubmit = (e) => {


    e.preventDefault();
    setFormValues(formValues);
    setFormErrors(validate(formValues));


    if(formValues.TMFixedCost!=='' && formValues.WeeklyCompletion!=='' && formValues.FeaturesCompleted!=='' && formValues.Dependencies!=='' && formValues.RisksMitigation!=='' && formValues.SupportRequired!==''){
      setSubmit(true);
      props.setShownOff(false)

    // console.log("output", formValues);
    }






    }





  //Form Validation Part//

  const validate = (values) => {
    const errors = {};

    if (!values.TMFixedCost.trim()) {
      errors.TMFixedCost = "*value required";
    }
    if (values.TMFixedCost < 0) {
      errors.TMFixedCost = "Enter valid T&M/Fixed Cost";
    }
    if (!values.WeeklyCompletion.trim()) {
      errors.WeeklyCompletion = "*value required";
    }
    if (!values.numberOfStories.trim()) {
      errors.numberOfStories = "*value required";
    }
    if (!values.FeaturesCompleted.trim()) {
      errors.FeaturesCompleted = "*value required";
    }

    if (!values.newBugs.trim()) {
      errors.newBugs = "*value required";
    }
    if (!values.bugsFixed.trim()) {
      errors.bugsFixed = "*value required";
    }
    if (!values.codeReview.trim()) {
      errors.codeReview = "*value required";
    }
    if (!values.unitTesting.trim()) {
      errors.unitTesting = "*value required";
    }
    if (!values.weeklyCommunication.trim()) {
      errors.weeklyCommunication = "*value required";
    }
    if (!values.delay.trim()) {
      errors.delay = "*value required";
    }
    if (!values.Dependencies.trim()) {
      errors.Dependencies = "*value required";
    }
    if (!values.RisksMitigation.trim()) {
      errors.RisksMitigation = "*value required";
    }
    if (!values.Risks.trim()) {
      errors.Risks = "*value required";
    }
    if (!values.SupportRequired.trim()) {
      errors.SupportRequired = "*value required";
    }

    return errors;
  };

  //----------------------------------------------------------------//


  const [designer,setDesigner]= useState(0);
  const[frontEnd,setFrontEnd]= useState(0);
  const[backEnd,setBackEnd]= useState(0);
  const[qa,setQA]= useState(0);




  const[total,setTotal] = useState(0);

  // total=designer+frontEnd+backEnd+qa;


  //  <CommonDropDown placeholder={total}/>
  useEffect(() => {
  <CommonDropDown placeholder={setTotal}/>
  },[total])


  if(props.output)
  {

  return
  (
    <div>
    <div className='option'>


         <div className='option_title'>
            <span className='option_title_Design'>Designer</span>

            <div className='option_title_performance'>
            <img className='option_title_performance_Plus'onClick={()=>{setDesigner(designer+1);setTotal(total+1)}} src={Plus}/>
            <span className='option_title_performance_count'>{designer} </span> 
            <img className='option_title_performance_Minus'  onClick={()=>{setDesigner(designer-1);setTotal(total-1)}}src={Plus}/> 
            </div>

        </div>



        <div className='option_title'>
            <span className='option_title_FrontEnd'>Front End Developer</span>

            <div className='option_title_performance'>
            <img className='option_title_performance_Plus' onClick={()=>{setFrontEnd(frontEnd+1);setTotal(total+1)}}src={Plus}/>
            <span className='option_title_performance_count'>{frontEnd}</span> 
            <img className='option_title_performance_Minus' onClick={()=>{setFrontEnd(frontEnd-1);setTotal(total-1)}}src={Plus}/> 
            </div>
        </div>

        <div className='option_title'>
            <span className='option_title_BackEnd'>Back End Developer</span>

            <div className='option_title_performance'>

            <img className='option_title_performance_Plus' onClick={()=>{setBackEnd(backEnd+1);setTotal(total+1)}}src={Plus}/>
            <span className='option_title_performance_count'>{backEnd}</span> 
            <img className='option_title_performance_Minus' onClick={()=>{setBackEnd(backEnd-1);setTotal(total-1)}} src={Plus}/> 
            </div>
        </div>

        <div className='option_title'>
            <span className='option_title_QA'>QA</span>

            <div className='option_title_performance'>
            <img className='option_title_performance_Plus' onClick={()=>{setQA(qa+1);setTotal(total+1)}}src={Plus}/>
            <span className='option_title_performance_count'>{qa}</span> 
            <img className='option_title_performance_Minus'  onClick={()=>{setQA(qa-1);setTotal(total+1)}} src={Plus}/> 
            </div>

        </div>

    </div>
    </div>
  )
}

  else {
    return("");

  };





  // WEEKLY FORM CREATION ///

  if(props.trigger)
{
  return (

    //Form Starting
    <form style={{
      position: "absolute",
      width: "144rem",
      top: "2rem",
      left: "10.5%",
      backgroundColor: "rgba(0,0,0,0.5",
    }}
      onSubmit={(e) => handleSubmit(e)}
      required="required"
    >
      {/* {console.log(formValues)} */}
      <div className='WeeklyUpdate'>

        <div className='WeeklyUpdate_Head'>
          <h1 className='WeeklyUpdate_Head_Title'>Weekly Update</h1>
        </div>



        <div className='WeeklyUpdate_Overalldetail'>

          <h1 className='WeeklyUpdate_Overalldetail_Title'>Overall Detail</h1>

          <div className='WeeklyUpdate_Overalldetail_Field'>
            <div className='WeeklyUpdate_Overalldetail_Field_All'>
              <label className='WeeklyUpdate_Overalldetail_Field_All_label'>T&M/Fixed Cost</label><br />
              <input className="WeeklyUpdate_Overalldetail_Field_All_input"
                type="number"
                placeholder='Enter T&M/Fixed Cost '
                value={formValues.TMFixedCost}
                onChange={(e) => handleChange(e, "TMFixedCost")}
              />
              <p className='ErrorMessage' >{formErrors.TMFixedCost}</p>
            </div>


            <div className='WeeklyUpdate_Overalldetail_Field_All' >
              <label className='WeeklyUpdate_Overalldetail_Field_All_label'>No.of resources used</label> <div className='Highlights'><span>10 resources allocated</span></div><br />
              <div className="WeeklyUpdate_Overalldetail_Field_All_input">
                <CommonDropDown   />

              </div>


            </div>

            <div className='WeeklyUpdate_Overalldetail_Field_All' >
              <label className='WeeklyUpdate_Overalldetail_Field_All_label'>Weekly% Completion</label><br />
              <input className="WeeklyUpdate_Overalldetail_Field_All_input"
                type="text"
                value={formValues.WeeklyCompletion}
                onChange={(e) => handleChange(e, "WeeklyCompletion")}
              />
              <p className='ErrorMessage' >{formErrors.WeeklyCompletion}</p>

            </div>
          </div>

        </div>




        <div className='WeeklyUpdate_Weeklydetail'>

          <h1 className='WeeklyUpdate_Weeklydetail_Title'>Weekly Detail</h1>

          <div className='WeeklyUpdate_Weeklydetail_Field'>

            <div className='WeeklyUpdate_Weeklydetail_Field_All'>
              <label className='WeeklyUpdate_Weeklydetail_Field_All_label'>No.of Features/Stories</label><br />
              <input className="WeeklyUpdate_Weeklydetail_Field_All_input"
                type="text"
                value={formValues.numberOfStories}
                onChange={(e) => handleChange(e, "numberOfStories")}
              />
               <p className='ErrorMessage' >{formErrors.numberOfStories}</p>

            </div>

            <div className='WeeklyUpdate_Weeklydetail_Field_All'>
              <label className='WeeklyUpdate_Weeklydetail_Field_All_label'>Features Completed</label> <div className='Highlights2'><span>Total 100 stories</span></div><br />
              <input className="WeeklyUpdate_Weeklydetail_Field_All_input"
                type="text"

                onChange={(e) => handleChange(e, "FeaturesCompleted")}
                value={formValues.FeaturesCompleted}
              />

              <p className='ErrorMessage' >{formErrors.FeaturesCompleted}</p>

            </div>

            <div className='WeeklyUpdate_Weeklydetail_Field_All' >
              <label className='WeeklyUpdate_Weeklydetail_Field_All_label'>New Bugs</label><br />
              <input className="WeeklyUpdate_Weeklydetail_Field_All_input"
                type="text"
                value={formValues.newBugs}
                onChange={(e) => handleChange(e, "newBugs")}
              />
               <p className='ErrorMessage' >{formErrors.newBugs}</p>

            </div>

            <div className='WeeklyUpdate_Weeklydetail_Field_All'>
              <label className='WeeklyUpdate_Weeklydetail_Field_All_label'>Bugs</label><br />
              <input className="WeeklyUpdate_Weeklydetail_Field_All_input"
                type="text"
                value={formValues.bugsFixed}
                onChange={(e) => handleChange(e, "bugsFixed")}
              />
               <p className='ErrorMessage' >{formErrors.bugsFixed}</p>

            </div>

            <div className='WeeklyUpdate_Weeklydetail_Field_All'>
              <label className='WeeklyUpdate_Weeklydetail_Field_All_label'>Code Review</label><br />
              <input className="WeeklyUpdate_Weeklydetail_Field_All_input"
                type="text"
                value={formValues.codeReview}
                onChange={(e) => handleChange(e, "codeReview")}
              />
               <p className='ErrorMessage' >{formErrors.codeReview}</p>

            </div>

            <div className='WeeklyUpdate_Weeklydetail_Field_All'>
              <label className='WeeklyUpdate_Weeklydetail_Field_All_label'>Unit Testing</label><br />
              <input className="WeeklyUpdate_Weeklydetail_Field_All_input"
                type="text"
                value={formValues.unitTesting}
                onChange={(e) => handleChange(e, "unitTesting")}
              />
               <p className='ErrorMessage' >{formErrors.unitTesting}</p>

            </div>

            <div className='WeeklyUpdate_Weeklydetail_Field_All' >
              <label className='WeeklyUpdate_Weeklydetail_Field_All_label'>Weekly Communication</label><br />
              <input className="WeeklyUpdate_Weeklydetail_Field_All_input"
                type="text"
                value={formValues.weeklyCommunication}
                onChange={(e) => handleChange(e, "weeklyCommunication")}
              />
               <p className='ErrorMessage' >{formErrors.FeaturesCompleted}</p>

            </div>
            <div className='WeeklyUpdate_Weeklydetail_Field_All'>
              <label className='WeeklyUpdate_Weeklydetail_Field_All_label'>Delay</label><br/>
              <input className="WeeklyUpdate_Weeklydetail_Field_All_input"
                type="text"

                onChange={(e) => handleChange(e, "delay")}
                value={formValues.delay}
              />

              <p className='ErrorMessage' >{formErrors.delay}</p>

            </div>

          </div>

        </div>



        <div className='WeeklyUpdate_Otherdetail'>

          <h1 className='WeeklyUpdate_Otherdetail_Title'>Other Detail</h1>

          <div className='WeeklyUpdate_Otherdetail_Field'>

            <div className='WeeklyUpdate_Otherdetail_Field_All'>
              <label className='WeeklyUpdate_Otherdetail_Field_All_label'>Dependencies</label><br />
              <input className="WeeklyUpdate_Otherdetail_Field_All_input"
                type="text"

                placeholder='Enter description'
                onChange={(e) => handleChange(e, "Dependencies")}
                value={formValues.Dependencies}
                formNoValidate
              />
              <p className='ErrorMessage' >{formErrors.Dependencies}</p>

            </div>
            <div className='WeeklyUpdate_Otherdetail_Field_All'>
              <label className='WeeklyUpdate_Otherdetail_Field_All_label'>Risks Mitigation</label><br />
              <input className="WeeklyUpdate_Otherdetail_Field_All_input"
                type="text"

                placeholder='Enter description'
                onChange={(e) => handleChange(e, "RisksMitigation")}
                value={formValues.RisksMitigation}
              />
              <p className='ErrorMessage' >{formErrors.RisksMitigation}</p>

            </div>
            <div className='WeeklyUpdate_Otherdetail_Field_All'>
              <label className='WeeklyUpdate_Otherdetail_Field_All_label'>Risks</label><br />
              <input className="WeeklyUpdate_Otherdetail_Field_All_input"
                type="text"

                placeholder='Enter description'
                onChange={(e) => handleChange(e, "Risks")}
                value={formValues.Risks}
              />
              <p className='ErrorMessage' >{formErrors.Risks}</p>

            </div>
            <div className='WeeklyUpdate_Otherdetail_Field_All'>
              <label className='WeeklyUpdate_Otherdetail_Field_All_label'>Support Required</label><br />
              <input className="WeeklyUpdate_Otherdetail_Field_All_input"
                type="text"

                placeholder='Enter description'
                onChange={(e) => handleChange(e, "SupportRequired")}
                value={formValues.SupportRequired}
              />
              <p className='ErrorMessage' >{formErrors.SupportRequired}</p>

            </div>

          </div>

        </div>



        <div className='WeeklyUpdate_SubmitButton'>

          <button className='Cancelbtn' onClick={e=>props.setShownOff(false)}>Cancel</button>     
          <button className="WeeklyUpdate_SubmitButton_Update" >Update</button>
        </div>







      </div>
    </form>

    //Form Ending
    // ) : <ProjectDetails/>;
  ) }
  else{
    "";
  };
}

export default WeeklyUpdate;
